locked!=2 AND did=#{did} AND eid NOT IN ( SELECT eid FROM travel_emp WHERE tid IN ( SELECT tid FROM travel WHERE ((sdate >= #{sdate} AND sdate <= #{edate}) OR (edate >= #{sdate} AND edate <= #{edate})) OR ( sdate < #{sdate} AND edate > #{edate}) ) )
AND ${column} LIKE #{keyWord}
ORDER BY hiredate DESC ,did LIMIT #{start},#{lineSize}

